<script lang="ts">
	import IngredientSelection from '$lib/components/dialog/ingredientSelection/ingredientSelection.svelte';
	import { PlusSolid } from 'flowbite-svelte-icons';
	import Text from '$lib/components/text/text.svelte';
	import GlassCard from '$lib/components/card/glassCard.svelte';
	import PrimaryButton from '$lib/components/button/primaryButton.svelte';

	const ingredients: {
		id: string;
		name: string;
		description?: string;
		s3Key?: string;
	}[] = [
		{
			s3Key:
				'data:image/jpeg;base64,',
			name: 'Pepper',
			description: 'This is a pepper',
			id: '01',
		},
	];

	let selectedIngredients = [...ingredients];
	let dialogVisible = false;
	let selectedIngredient:
		| undefined
		| {
				id: string;
				name: string;
				description?: string;
				s3Key?: string;
		  };

	$: if (selectedIngredient) {
		const alreadySelectedTool = selectedIngredients.find(({ id }) => id === selectedIngredient?.id);
		if (!alreadySelectedTool) selectedIngredients.push(selectedIngredient);
		selectedIngredient = undefined;
	}
</script>

{#if dialogVisible}
	<IngredientSelection bind:dialogVisible bind:selectedIngredient />
{/if}

<div class="flex h-full w-full flex-col gap-4 p-4">
	<Text classNames="relative flex h-16 flex-col justify-center p-2">
		<span class="text-lg font-bold">Now add ingredients: </span>
	</Text>
	{#each selectedIngredients as { s3Key, name }}
		<div class="flex h-16 w-full flex-row">
			<div class="aspect-square w-16">
				<img class="h-full w-full object-cover" src={s3Key} alt="" />
			</div>
			<div class="flex grow flex-row">
				<span class="h-full w-full content-center text-center">{name}</span>
			</div>
		</div>
	{/each}
	<PrimaryButton
		classNames="h-16 w-16 aspect-square rounded-full"
		on:click={() => {
			dialogVisible = true;
		}}
	>
		<PlusSolid class="h-full w-full outline-none"></PlusSolid>
	</PrimaryButton>
</div>
